import React from "react";
import { BrowserRouter as Router, Route, Switch, withRouter, NavLink } from "react-router-dom" 
// import ScrollAnimation from "react-animate-on-scroll";
import "./App.css"
import Home from "./components/Home/Home"
import LazyLoad from "react-lazyload"; 
// import MediaQuery from 'react-responsive';
import { useMediaQuery } from 'react-responsive'

const Desktop = ({ children }) => {
    const isDesktop = useMediaQuery({ minWidth: 768 })
    return isDesktop ? children : null
  }
  const Tablet = ({ children }) => {
    const isTablet = useMediaQuery({ minWidth: 768, maxWidth: 991 })
    return isTablet ? children : null
  }
  const Mobile = ({ children }) => {
    const isMobile = useMediaQuery({ maxWidth: 767 })
    return isMobile ? children : null
  }
  const Default = ({ children }) => {
    const isNotMobile = useMediaQuery({ minWidth: 768 })
    return isNotMobile ? children : null
  }




const App = () => {
    const isTabletOrMobile = useMediaQuery({ minDeviceWidth: 1224 },
        { deviceWidth: 1600 } )

    return (
            <div>
                <Desktop>
                <nav>
                    <h1>DREAM YOUTH INTERNATIONAL</h1>
                    <h6>DREAM YOUTH INTERNATIONAL: Promoting Youth, Education and Project Managements in Africa</h6>
                </nav>
                <Router>
                    <NavLink
                            to="/"
                            activeClassName="is-active"
                            exact={true}
                            style={{ color: "#3BB1EB", fontWeight: "bold"}}

                    >
                        Home
                    </NavLink>

                    <Switch>
                        <Route path="/" component={withRouter(Home)} exact></Route>
                    </Switch>
                </Router>
                </Desktop>
                        
                
            }
        </div>
        
    );
}

export default App
