import React from "react";
import { BrowserRouter as Router, Route, Switch, withRouter, NavLink } from "react-router-dom" 
import ScrollAnimation from "react-animate-on-scroll";
import "./App.css"
import Home from "./components/Home/Home"
import LazyLoad from "react-lazyload"; 

const Spinner = () => (
    <div className= "post loading">
        <h5>Loading...</h5>
    </div>
)




const App = () => {
    return (
        <LazyLoad
            height={500}
            offset={[-100, 100]}
            placeholder={<Spinner />}
        >
            <div>
                <ScrollAnimation 
                    duration={10}
                    animateIn='fadeIn'
                    animateOut='fadeOut'
                    // scrollableParentSelecor='#scrolly-div-animatePreScroll'
                    // animatePreScroll={false}
                    >
                <nav>
                    <h1>DREAM YOUTH INTERNATIONAL</h1>
                    <h6>DREAM YOUTH INTERNATIONAL: Promoting Youth, Education and Project Managements in Africa</h6>
                </nav>
                <Router>
                        <NavLink
                                to="/"
                                activeClassName="is-active"
                                exact={true}
                                style={{ color: "white"}}

                        >
                            Home
                        </NavLink>

                        <Switch>
                            <Route path="/" component={withRouter(Home)} exact></Route>
                        </Switch>
                    </Router>
                    
                    
                </ScrollAnimation>
            
            </div>
        </LazyLoad>
        
    );
}

export default App
