import React from "react";
import { BrowserRouter as Router, Route, Switch, withRouter, NavLink } from "react-router-dom" 
// import ScrollAnimation from "react-animate-on-scroll";
import "./App.css"
import Home from "./components/Home/Home"
import LazyLoad from "react-lazyload"; 
// import MediaQuery from 'react-responsive';
import { useMediaQuery } from 'react-responsive'

  const isDesktopOrLaptop = useMediaQuery({ minDeviceWidth: 1224 })
  const isBigScreen = useMediaQuery({ minDeviceWidth: 1824 })
  const isTabletOrMobileDevice = useMediaQuery({ maxDeviceWidth: 1224 })
  const isPortrait = useMediaQuery({ orientation: 'portrait' })
  const isRetina = useMediaQuery({ minResolution: '2dppx' })

const Spinner = () => (
    <div className= "post loading">
        <h5>Loading...</h5>
    </div>
)




const App = () => {
    const isTabletOrMobile = useMediaQuery({ minDeviceWidth: 1224 },
        { deviceWidth: 1600 } )

    return (
            <div>
                {isTabletOrMobile &&
                <div>
                        <nav>
                    <h1>DREAM YOUTH INTERNATIONAL</h1>
                    <h6>DREAM YOUTH INTERNATIONAL: Promoting Youth, Education and Project Managements in Africa</h6>
                </nav>
                <Router>
                    <NavLink
                            to="/"
                            activeClassName="is-active"
                            exact={true}
                            style={{ color: "#3BB1EB", fontWeight: "bold"}}

                    >
                        Home
                    </NavLink>

                    <Switch>
                        <Route path="/" component={withRouter(Home)} exact></Route>
                    </Switch>
                </Router>
                </div>
                
            }
        </div>
        
    );
}

export default App
